import { Component, OnInit } from '@angular/core';
import { ServerDataService } from '../server-data.service';
import { Server } from './server';

@Component({
  selector: 'app-server',
  templateUrl: './server.component.html',
  styleUrls: ['./server.component.css'],
})
export class ServerComponent implements OnInit {
  servers: Server[] = [];

  constructor(private serverDataService: ServerDataService) {}

  ngOnInit(): void {
    this.servers = this.serverDataService.getServers();
  }

  clearServers() {
    this.serverDataService.clearServers();
  }
}
<div class="container mt-4">
  <h2 class="text-center">Server List</h2>
  <table class="table">
    <thead>
      <tr>
        <th scope="col">ID</th>
        <th scope="col">Name</th>
        <th scope="col">Language</th>
        <th scope="col">Framework</th>
        <th scope="col">Modify</th>
      </tr>
    </thead>
    <tbody>
      <!-- Loop through the 'servers' array and populate table rows -->
      <tr *ngFor="let server of servers">
        <td>{{ server.id }}</td>
        <td>{{ server.name }}</td>
        <td>{{ server.language }}</td>
        <td>{{ server.framework }}</td>
        <td>
          <!-- Add buttons or actions for modifying the server here -->
          <button class="btn btn-warning mx-1">Edit</button>
          <button class="btn btn-danger mx-1">Delete</button>
        </td>
      </tr>
    </tbody>
  </table>
</div>
