import { Component, ViewChild, ElementRef } from '@angular/core';
import { MatDialogRef } from '@angular/material/dialog';
import { ServerService } from '../server.service';
import { Server } from '../server';

@Component({
  selector: 'app-create-server',
  templateUrl: './create-server.component.html',
  styleUrls: ['./create-server.component.css']
})
export class CreateServerComponent {
  newServer: Server = new Server();
  showSuccessMessage = false;

  @ViewChild('successAlert') successAlert!: ElementRef;

  constructor(
    private dialogRef: MatDialogRef<CreateServerComponent>,
    private serverService: ServerService
  ) {}

  createServer() {
    this.serverService.createServer(this.newServer).subscribe(
      (data: Server) => {
        console.log('Server created:', data);

        // Show the success message
        this.showSuccessMessage = true;

        // Close the dialog after a delay
        setTimeout(() => {
          this.dialogRef.close();
        }, 2000); // Close the dialog after 2 seconds (2000 milliseconds)
      },
      (error) => {
        console.error('Error creating server:', error);
        // Handle error cases here if needed
      }
    );
  }

  closeDialog() {
    // Close the dialog without creating a server
    this.dialogRef.close();
  }
}
<div class="mat-dialog-content">
  <h2>Create Server</h2>
  <form #serverForm="ngForm">
    <div class="form-group">
      <label for="id">ID</label>
      <input type="text" id="id" name="id" [(ngModel)]="newServer.id" required>
    </div>
    <div class="form-group">
      <label for="name">Name</label>
      <input type="text" id="name" name="name" [(ngModel)]="newServer.name" required>
    </div>
    <div class="form-group">
      <label for="language">Language</label>
      <input type="text" id="language" name="language" [(ngModel)]="newServer.language" required>
    </div>
    <div class="form-group">
      <label for="framework">Framework</label>
      <input type="text" id="framework" name="framework" [(ngModel)]="newServer.framework" required>
    </div>
  </form>
</div>
<div class="mat-dialog-actions">
  <button mat-button (click)="createServer()" [disabled]="!serverForm.valid">Create</button>
  <button mat-button (click)="closeDialog()">Cancel</button>
</div>

<div class="alert alert-success mt-3" #successAlert role="alert" [hidden]="!showSuccessMessage">
  Server Created Successfully
</div>
