import { Component, Inject } from '@angular/core';
import { MAT_DIALOG_DATA, MatDialogRef } from '@angular/material/dialog';
import { Server } from '../server';
import { ServerService } from '../server.service';

@Component({
  selector: 'app-edit-server',
  templateUrl: './edit-server.component.html',
  styleUrls: ['./edit-server.component.css']
})
export class EditServerComponent {
  editedServer: Server;

  constructor(
    @Inject(MAT_DIALOG_DATA) private data: any,
    private dialogRef: MatDialogRef<EditServerComponent>,
    private serverService: ServerService
  ) {
    // Initialize the editedServer with the selected server data
    this.editedServer = { ...data.server };
  }

  onSubmit() {
    // Send an HTTP request to update the server data on the server
    this.serverService.updateServer(this.editedServer).subscribe((updatedServer: Server) => {
      // Close the dialog and pass the updated server data back
      this.dialogRef.close(updatedServer);
    });
  }

  onCancel() {
    // Close the dialog without saving changes
    this.dialogRef.close();
  }
}
<div class="mat-dialog-content">
  <h2>Edit Server</h2>
  <form #editServerForm="ngForm" (ngSubmit)="onSubmit()">
    <div class="form-group">
      <label for="id">ID</label>
      <input type="text" id="id" name="id" [(ngModel)]="editedServer.id" required>
    </div>
    <div class="form-group">
      <label for="name">Name</label>
      <input type="text" id="name" name="name" [(ngModel)]="editedServer.name" required>
    </div>
    <div class="form-group">
      <label for="language">Language</label>
      <input type="text" id="language" name="language" [(ngModel)]="editedServer.language" required>
    </div>
    <div class="form-group">
      <label for="framework">Framework</label>
      <input type="text" id="framework" name="framework" [(ngModel)]="editedServer.framework" required>
    </div>
    <div class="mat-dialog-actions">
      <button mat-button (click)="onSubmit()" [disabled]="!editServerForm.valid">Update</button>
      <button mat-button (click)="onCancel()">Cancel</button>
    </div>
  </form>
</div>
